INSERT INTO `options_option` VALUES ('html_title', 'Измените настройки', 'Название сайта. Размещается в заголовке окна браузера.', '', 1);
INSERT INTO `options_option` VALUES ('html_description', '', 'Краткая аннотация содержания документа. Используется поисковыми системами для описания документа. Задание этого значения полезено в случаях, когда в документе мало текста, когда это управляющий фреймами файл (frameset) или в начале документа используются скрипты. Длина текста до 100 символов.', '', 1);
INSERT INTO `options_option` VALUES ('html_keywords', '', 'Используется поисковыми системами для индексирования документа. Обычно здесь указываются синонимы к словам в заголовке (title) или альтернативный заголовок. Длина списка до 1000 символов. Не допускается использование одного и того же ключевого слова более 7 раз, поисковые системы просто будут игнорировать это слово', '', 1);
